    public static List<Integer> findSubstring(String s, String[] words) {
        List<Integer> results = new ArrayList<>();
        boolean[] flags = new boolean[words.length];
        doFindSubString2(s, words, results, 0, flags, new StringBuilder());
        return results;
    }


    public static boolean isSubStrPosition(String s, int offset, String[] words, boolean[] flags, int depth){
        if(depth == words.length){
            return  true;
        }
        for(int i=0; i<words.length; i++){
            if(!flags[i]){
                if(s.startsWith(words[i], offset)) {
                    flags[i] = true;
                    if (isSubStrPosition(s, offset + words[i].length(), words, flags, depth + 1)){
                        return  true;
                    }
                    flags[i] = false;
                }
            }
        }
        return false;
    }


    public static boolean isSubStrPosition2(String s, int offset, String[] words, boolean[] flags, int depth){
        for(int i=0; i<words.length; i++){
            int m=0;
            for(; m<words.length; m++){
                if(s.startsWith(words[m], offset)){
                    if(!flags[m]){
                        flags[m] = true;
                        offset += words[m].length();
                        break;
                    }
                }
            }
            if(m == words.length){
                return false;
            }
        }
        return true;
    }


    public static  void doFindSubString2(String s, String[] words, List<Integer> results, int depth, boolean[] flags, StringBuilder sb){
        int len = s.length() - words.length*words[0].length() + 1;
        for(int i=0; i < len; i++){
             int offset = i;
             boolean  matchWords = isSubStrPosition2(s, offset, words, flags, 0);
             if(matchWords) {
                 results.add(i);
             }
             for(int n=0; n<flags.length; n++){
                 flags[n] = false;
             }
        }
    }

    public static  void doFindSubString(String s, String[] words, List<Integer> results, int depth, boolean[] flags, StringBuilder sb){
        if(depth == words.length){
            String substr = sb.toString();
            int index = s.indexOf(substr);
            while(index >= 0){
                if(results.indexOf(index) < 0){
                    results.add(index);
                }
                index = s.indexOf(substr, index + 1);
            }
            return;
        }
        for(int i=0; i<words.length; i++){
            if(!flags[i]){
                String word = words[i];
                sb.append(word);
                flags[i] = true;
                if(s.indexOf(sb.toString()) >= 0) {
                    doFindSubString(s, words, results, depth + 1, flags, sb);
                }
                sb.delete(sb.length() - word.length(), sb.length());
                flags[i] = false;

            }
        }
    }


