    private static void mergeSort(int[] dest, int wordLen, String src, int[] aux, int low, int high){
        if(low >= high){
            return;
        }
        int mid = (low + high)>>>1;
        mergeSort(dest, wordLen, src, aux, low, mid);
        mergeSort(dest, wordLen, src, aux, mid+1, high);
        int li=low, hi = mid + 1;
        int k = li;
        while (li <= mid && hi <= high){
            int  val  =  0;
            for(int w=0; w<wordLen; w++){
                char liChar = src.charAt(w + aux[li]);
                char hiChar = src.charAt(w + aux[hi]);
                val = liChar - hiChar;
                if(val != 0){
                    break;
                }
            }
            if(val > 0){
                aux[k++] = dest[hi++];
            }else{
                aux[k++] = dest[li++];
            }
        }
        while (li <= mid){
            aux[k++] = dest[li++];
        }
        while (hi <= high){
            aux[k++] = dest[hi++];
        }
        if(low == 0 && high == aux.length - 1){
            return;
        }
        for(int i=low; i<=high;i++){
            dest[i] = aux[i];
        }
    }


