    public static  void doFindSubString6(String s, final String[] words, List<Integer> results, int depth, boolean[] flags, StringBuilder sb){
        if(s == null || s.length() == 0 || words == null || words.length == 0){
            return;
        }
        int len = s.length() - words.length*words[0].length() + 1;
        int wordLength = words[0].length();
        int allWordLength = words.length*wordLength;
        int wordsSum = 0;
        int sSum = 0;
        Node[] wordNodes = new Node[words.length];
        Node[] sWordNodes = new Node[words.length];
        Node[] searchWordNodes = new Node[words.length];
        for(int i=0; i<words.length; i++){
            for(int m=0; m<wordLength; m++){
                char ch = words[i].charAt(m);
                wordsSum += ch;
                int si = i*wordLength + m;
                if(si < s.length()){
                    char sch = s.charAt(si);
                    sSum += sch;
                }
            }
            sWordNodes[i] = new Node();
            sWordNodes[i].wordLen = wordLength;
            sWordNodes[i].src = s;
            wordNodes[i] = new Node();
            wordNodes[i].wordLen = wordLength;
            wordNodes[i].src = words[i];
            wordNodes[i].offset = 0;

        }
        Arrays.sort(wordNodes);
        for(int i=0; i < len; i++){
            if(i > 0){
                char r = s.charAt(i-1);
                char a =  s.charAt(allWordLength + i - 1);
                sSum -= r;
                sSum += a;
            }
            if(sSum == wordsSum){
                int offset = i;
                for(int m=0; m<words.length; m++){
                    sWordNodes[m].offset = offset;
                    offset+=wordLength;
                    searchWordNodes[m] = wordNodes[m];
                }
                boolean  matchWords = true;
                for(int m=0; m<words.length; m++){
                    int high =  words.length-1-m;
                    int index = binearSearch(searchWordNodes, searchWordNodes[m], flags, 0, high);
                    if(index < 0){
                        matchWords = false;
                        break;
                    }
                    //move to front
                    for(int n=index; n<high; n++){
                        searchWordNodes[n] = searchWordNodes[n+1];
                    }
                }
                //Arrays.sort(sWordNodes);
                //boolean  matchWords = Arrays.equals(wordNodes, sWordNodes);
                if(matchWords) {
                    results.add(i);
                }
            }
        }
    }

    private static int binearSearch(Node[] nodes, Node target, boolean[] flags, int low, int high){
        if(low >= high){
            boolean eq = nodes[low].equals(target);
            if(eq){
               return low;
            }
            return -1;
        }
        int mid = (low + high)/2;
        int left = binearSearch(nodes, target, flags, low, mid);
        if(left >= 0 ){
            return left;
        }
        int right = binearSearch(nodes, target, flags, mid + 1, high);
        if(right >= 0){
            return right;
        }
        return -1;
    }



    public static class Node implements  Comparable<Node>{
        public int offset;
        public String src;
        public int wordLen;
        private int hash;

        @Override
        public int compareTo(Node b) {
            int val = 0;
            for(int w=0; w<wordLen; w++){
                char liChar = src.charAt(w  + this.offset);
                char hiChar = b.src.charAt(w + b.offset);
                val = liChar - hiChar;
                if(val != 0){
                    break;
                }
            }
            return val;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            Node node = (Node) o;
            return compareTo(node) == 0;
        }

        @Override
        public int hashCode() {
            int h = hash;
            if (h == 0) {
                for(int i=0; i<wordLen; i++){
                    h = 31*h + src.charAt(i + offset);
                }
                hash = h;
            }
            return h;
        }


    }



